-- ----------------------------
-- Table structure for tb_yiduihuan_notice
-- ----------------------------
DROP TABLE IF EXISTS `tb_yiduihuan_notice`;
CREATE TABLE `tb_yiduihuan_notice` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `title` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '标题',
  `summary` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '内容摘要',
  `contents` text COLLATE utf8_unicode_ci COMMENT '内容',
  `releaseTime` datetime DEFAULT NULL COMMENT '发布时间',
  `editTime` datetime DEFAULT NULL COMMENT '最后更新时间',
  `editor` int(11) DEFAULT NULL COMMENT '编辑ID',
  `display` smallint(1) NOT NULL DEFAULT '1' COMMENT '显示状态',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=31 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

-- ----------------------------
