-- ----------------------------
-- Table structure for tb_yiduihuan_user
-- ----------------------------
DROP TABLE IF EXISTS `tb_yiduihuan_user`;
CREATE TABLE `tb_yiduihuan_user` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `mobile` varchar(11) COLLATE utf8_unicode_ci NOT NULL COMMENT '手机号',
  `name` varchar(50) COLLATE utf8_unicode_ci NOT NULL,
  `email` varchar(100) COLLATE utf8_unicode_ci NOT NULL COMMENT '邮箱',
  `password` varchar(255) COLLATE utf8_unicode_ci NOT NULL COMMENT '加密后密码',
  `qq` varchar(12) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'QQ',
  `mailVerify` smallint(1) DEFAULT '0' COMMENT '邮箱验证',
  `regIP` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '注册IP',
  `regDate` datetime DEFAULT NULL COMMENT '注册时间',
  `salt` varchar(6) COLLATE utf8_unicode_ci NOT NULL COMMENT '加密钥',
  `loginTimes` int(11) NOT NULL DEFAULT '1' COMMENT '登录次数',
  `errorTimes` int(11) NOT NULL DEFAULT '0' COMMENT '错误次数',
  `question` varchar(50) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '密保问题',
  `answer` varchar(50) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '密保答案',
  `groupId` int(11) DEFAULT NULL COMMENT '用户组ID',
  `c` int(2) NOT NULL DEFAULT '1' COMMENT '公司编号',
  PRIMARY KEY (`id`),
  UNIQUE KEY `email` (`email`),
  UNIQUE KEY `mobile` (`mobile`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=10001 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci ROW_FORMAT=DYNAMIC;

